DROP DATABASE bn6library;
CREATE DATABASE bn6library;
USE bn6library;

CREATE TABLE battlechips(
   id          INT NOT NULL AUTO_INCREMENT
  ,name        VARCHAR(9) NOT NULL
  ,element     ENUM('FIRE', 'AQUA', 'ELEC', 'WOOD', 'SWORD', 'WIND', 'CURSOR', 'SUMMON', 'BONUS_POINT', 'BREAK', 'NONE') NOT NULL
  ,mb          INT  NOT NULL
  ,atk         INT  NOT NULL
  ,codes       VARCHAR(7) NOT NULL
  ,description VARCHAR(150)
  ,image       VARCHAR(100)
  ,alias       VARCHAR(150)
  ,

  PRIMARY KEY(id)
);


